var redVal = 0;
var greenVal = 0;
var blueVal = 0;

function changeCol(evt) {
   redVal = Math.round(Math.random() * 255);
   greenVal = Math.round(Math.random() * 255);
   blueVal = Math.round(Math.random() * 255);
   evt.target.setAttribute("fill",
         "rgb(" + redVal + "," + greenVal + "," + blueVal + ")");

}
